 * For more details on building Java & JVM projects, please refer to https://docs.gradle.org/8.2/userguide/building_java_projects.html in the Gradle documentation.
 */

plugins {
    // Apply the application plugin to add support for building a CLI application in Java.
    application
    id("com.google.protobuf") version "0.9.3"
}

repositories {
    // Use Maven Central for resolving dependencies.
    mavenCentral()
}

dependencies {
    // mRPC Java
    implementation(files("libs/mrpc-java.jar"))

    // mRPC Java dependencies
    implementation("com.google.protobuf:protobuf-java:3.25.0")
    implementation("io.socket:socket.io-client:2.1.0")
    implementation("io.socket:socket.io-server:4.1.2")
    implementation("jakarta.servlet:jakarta.servlet-api:6.0.0")
    implementation("org.eclipse.jetty:jetty-server:11.0.18")
    implementation("org.eclipse.jetty:jetty-servlet:11.0.18")
}

// Apply a specific Java toolchain to ease working on different environments.
java {
    toolchain {
        languageVersion.set(JavaLanguageVersion.of(11))
    }
}

application {
    // Define the main class for the application.
    mainClass.set("com.example.javacli.ClientApp")
}

protobuf {
    protoc {
        artifact = "com.google.protobuf:protoc:3.0.0"
        path = "/opt/homebrew/bin/protoc"
    }
    generateProtoTasks {
        ofSourceSet("main").forEach {
            it.builtins {
                java {}
            }
        }
    }
}
